import React from 'react'
import { NavList,LinkStyled } from "./Nav.styles"

const Links = [
    { to: "/", text: 'Home' },
    {to:"/starred",text:"Starred"}
]


const Navigation = () => {
  return (
      <div>
          <NavList>
              {
                  Links.map(item => <LinkStyled key={item.text} to={item.to}>{ item.text}</LinkStyled>)
              }
          </NavList>
    </div>
  )
}

export default Navigation