import React from 'react'
import notfound from "images/not-found.png"

export type ActorProps = {
    person: {
        id: number,
        name: string,
        image: {
            medium: string,
            original:string,
        },
        country: {
            name:string
        }
        birthday: string,
        gender: string,
        deathday:string,

    }

}
const ActorCard = ({ person: { image, gender,name, country, birthday, deathday } }: ActorProps) => {
    const imgSrc = image ? image.medium : notfound

   return  <div>
      <div>
        <img src={imgSrc} alt="actor" />
      </div>
      <h1>
        {name} {gender ? `(${gender})` : null}
      </h1>
      <p>{country ? `Comes from ${country.name}` : 'No country known'}</p>
      {birthday ? <p>Born {birthday}</p> : null}
      <p>{deathday ? `Died ${deathday}` : 'Alive'}</p>
    </div>
}

export default ActorCard