import React from 'react'
import { FlexGrid } from '../../../style'
import ActorCard, {ActorProps} from "./ActorCard"

type ActorListProps = {
    data:ActorProps []
}

const ActorList = ({data}:ActorListProps) => {
  return (
      <FlexGrid>{data.map(item => <ActorCard key={item.person.id} {...item}/>)}</FlexGrid>
  )
}

export default ActorList