import React from 'react'
import { ActorProps } from "components/actor/ActorCard"
import notFound from "../../images/not-found.png"
import {CastList,} from "./Cast.styled"

type Casts = {
    casts:ActorProps []
}

const Cast = ({ casts }: Casts) => {
  return (
    <CastList>
      {casts.map(({ person, character, voice }, key) => (
        <div key={key} className='cast-item'>
          <div className='pic-wrapper' >
            <img
              src={person.image ? person.image.medium : notFound}
              alt="cast-person"
            />
          </div>
          <div className='actor'>
            <span>
              <span className='bold'>{person.name}</span> | {character.name} {voice ? '| Voice' : ''}
            </span>
          </div>
        </div>
      ))}
    </CastList>
  )
}

export default Cast