import React from 'react'
import { ActorProps } from "components/actor/ActorCard"
import notFound from "../../images/not-found.png"

type Casts = {
    casts:ActorProps []
}

const Cast = ({ casts }: Casts) => {
  return (
    <div>
      {casts.map(({ person, character, voice }, key) => (
        <div key={key}>
          <div>
            <img
              src={person.image ? person.image.medium : notFound}
              alt="cast-person"
            />
          </div>
          <div>
            <span>
              {person.name} | {character.name} {voice ? '| Voice' : ''}
            </span>
          </div>
        </div>
      ))}
    </div>
  )
}

export default Cast