import React from 'react'

type DetailProps = {
    status: string,
    network: {name:string},
    premiered:string
}

const Detail = ({status,premiered,network}:DetailProps) => {
   return (
    <div>
      <p>
        Status: <span>{status}</span>
      </p>
      <p>
        Premiered {premiered} {network ? `on ${network.name}` : null}
      </p>
       </div>
   )
}

export default Detail