import React from 'react'
import {DetailsWrapper} from "./Detail.style"

type DetailProps = {
    status: string,
    network: {name:string},
    premiered:string
}

const Detail = ({status,premiered,network}:DetailProps) => {
   return (
    <DetailsWrapper>
      <p>
        Status: <span>{status}</span>
      </p>
      <p>
        Premiered {premiered} {network ? `on ${network.name}` : null}
      </p>
       </DetailsWrapper>
   )
}

export default Detail