import React from 'react'
import { FlexGrid } from '../../../style'
import ShowCard, { ShowProps } from "./ShowCard"

type ShowListProps = {
    data:ShowProps []
}

const ShowList = ({data}:ShowListProps) => {
  return (
      <FlexGrid>{
          data.map(item => <ShowCard key={item.show.id} {...item}/>)
      }</FlexGrid>
  )
}

export default ShowList