import React from 'react'
import notFound from "images/not-found.png"
import { Star } from "../../../style"
import {MainDataWrapper,Headline,TagList} from "./ShowMainData.style"

type ShowMainDataProps= {
  image: {
    medium: string,
    original:string
    }
    name: string;
    rating: {average:number}
    summary: string
    tags:string[]
}

const ShowMainData = ({image,name,rating,summary,tags}:ShowMainDataProps) => {
   return (
    <MainDataWrapper>
      <img src={image ? image.original : notFound } alt="show-cover" />
      <div className='text-side'>
        <Headline>
          <h1>{name}</h1>
          <div>
            <Star />
            <span>{rating.average || 'N/A'}</span>
          </div>
        </Headline>
        <div className='summary' dangerouslySetInnerHTML={{ __html: summary }} />

        <div>
          Tags:{' '}
          <TagList>
            {tags.map((tag, i) => (
              <span key={i}>{tag}</span>
            ))}
          </TagList>
        </div>
      </div>
    </MainDataWrapper>
  );
}

export default ShowMainData