import React, { useEffect, useState } from 'react'
import MainPageLayout from "layout/MainPageLayout"
import { useShows } from "../misc/custom-hook"
import { apiGet } from "../misc/config"
import ShowsList from "components/show/ShowList"

const Starred = () => {
  const {state} = useShows()
  const [shows, setShows] = useState<any []>([])
  const [isLoading, setIsloading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    if (state && state.length > 0) {
      const promises = state.map(showId => apiGet(`/shows/${showId}`));

      Promise.all(promises).then(apiData => apiData.map(show => ({ show: { ...show } }))).then(results => {
        setShows(results)
        setIsloading(false)

      }).catch(err => {
        setIsloading(false)
        setError(err.message)
      })
    }

  }, [])


  return (
    <MainPageLayout>
      {isLoading && <div>Shows are still loading</div>}
      {error && <div> Error occured :{error}</div>}
      {!isLoading && !shows && <div>No shows were added</div>}
      {!isLoading && !error && shows && <ShowsList data={shows} />}
    </MainPageLayout>
  )
}

export default Starred